package EjemplosCiclos;

public class whileEjemplo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		while (a<=4) {//Salir del bucle cuando 'a' es mayor que 4
			System.out.println("El valor de a: " + a);
			a++;//inclrementa el valor de 'a' para la siguiente iteración
			
		}

	}

}
